import uuidimport streamlit as stdef login():    st.title("User Login" )    user_details = [            {                "id" : uuid.uuid4(),                 "username" : "test1",                  "password" : "pass"                            },             {                "id" : uuid.uuid4(),                "username" : "test2",                "password" : "pass2"            }        ]        username = st.text_input("Enter Username")    password = st.text_input ("Enter Password", type='password')        if st.button("Login"):                for item in user_details:            if username == item['username']:                if password == item['password']:                    st.success("Login Successful")                                    else:                    st.warning("Password is incorrect")           if st.button("Sign Up"):        if username == "" or password == "":            st.warning("Username or password cannot be blank")        else:            user_details.append({                "id" : uuid.uuid4(),                "username" : username,                "password": password})            st.success("Sign up successful!")    